//use a pure reducer function
const initialState={
    searches:[],
    results:[]
}
//(previous state, action), avoid mutating state
const reducer = (state=initialState,action)=>{
    switch (action.type){
        case SEARCHING:
            return{
                ...state,
                searches: [...state.searches,action.data]
            }
        case OBTAIN_DATA:
            return{
                ...state,
                results:action.resultingData
            }
        case ERROR:
            return state
        default:
            return state
    }
}

export default reducer;
