import React, { Component } from 'react';
import { connect } from 'react-redux';
import {fetchResults} from '../../actions/resultsDispatch';
import {search} from '../../actions/searchDispatch';

class Navigate extends Component{
    state={
        currentSearchInput:""
    }
    handleInputChange = (e) =>{
        let newSearchInput=e.target.value;
        this.setState({
            currentSearchInput:newSearchInput
        })
    }
    handleFormSubmit = (e) =>{
        e.preventDefault();
        this.props.search(this.state.currentSearchInput);
        this.props.fetchResults(this.state.currentSearchInput);
    }
    render(){
        return(
            <nav>
                <a href="/">Hacker News</a>
                <button type="button">
                    <span></span>
                </button>
                <div>
                    <ul>
                        <li>
                            <form onSubmit={this.handleFormSubmit}>
                                <input type="search" onChange={this.handleInputChange}></input>
                                <button type="submit">Search</button>
                            </form>
                        </li>
                    </ul>
                </div>
            </nav>
        )
    }
}
const mapStateToResults = (state) =>{
    return{
        searches: state.searches,
        resultingState: state.resultingState
    }
}

const mapDispatchToResults = (dispatch)=>{
    return{
        search: (data)=> {dispatch(search(data))},
        fetchResults: (query)=> dispatch(fetchResults(query))
    }
}

export default connect(mapStateToResults,mapDispatchToResults)(Navigate);
