import React, {Component} from 'react';
import {connect} from 'react-redux';

class Search extends Component {

    searchResults= ()=>{
        if(this.props.resultingData.length===0){
            return(
                <div>
                    <strong>Do a search</strong>
                </div>
            )
        }else{
            const {resultingData}=this.props;
            return(
                <div>
                    {resultingData.map(result=>{
                        return(
                            <a href={result.url} 
                            className="list-group-item list-group-item-action list-group-item-info mt-1 mb-1"
                            key={result.objectID}
                            >
                                <strong className="text-dark">{result.title}</strong>
                                <br></br>
                                <strong>Author:</strong>
                                {result.author}
                            </a>
                        )
                    })}
                </div>
            )
        }
    }
    render(){
        return(
            <div className="search-output">
                <div className="row">
                    <div className="col-12">
                        <h1 className="text-dark">Search Output</h1>
                    </div>
                </div>
                <div className="row m-3">
                    <div className="col-12 p-1 bg-light border border-info">
                        {this.searchResults()}
                    </div>
                </div>
            </div>
        )
    }
}

const sendStateToProps = (state) =>{
    return{
        resultingData:state.results
    }
}

export default connect(sendStateToProps)(Search)
